targets.expr:
(sum(rate(amax_client_messages_discarded_total{
    organization="$organization",environment=~"$environments",application="$application",type="incoming",reason="noSubscriptionMatch"
}[$__interval:1m])) by (organization, application) /
sum(rate(amax_client_messages_sent_total{
    organization="$organization",environment=~"$environments",application="$application"
}[$__interval:1m])) by (organization, application) > 0)
or sum(amax_application_info{organization="$organization",application="$application"}) by (organization,application) * 0

